select
d.dtu_id,
m.name as equipment_model_name,

from eam_equipment t
LEFT JOIN eam_equipment_model m ON t.equipment_model_id = m.equipment_model_id
LEFT JOIN eam_equipment_company e ON t.equipment_id = e.equipment_id
LEFT JOIN eam_dtu_equipment d ON t.equipment_id = d.equipment_id AND d.delete_flag = 0
WHERE t.delete_flag = 0 AND e.delete_flag = 0

	AND e.equipment_category_id = #{equipmentCategoryId}


	AND e.company_id = #{companyId}


	order by ${orderByClause}



		limit ${offset}, ${limit}


		limit ${limit}

select count(*)
from eam_equipment t
LEFT JOIN eam_equipment_company e ON t.equipment_id = e.equipment_id
LEFT JOIN eam_dtu_equipment d ON t.equipment_id = d.equipment_id AND d.delete_flag = 0
WHERE t.delete_flag = 0 AND e.delete_flag = 0

	AND e.equipment_category_id = #{equipmentCategoryId}


	AND e.company_id = #{companyId}


	order by ${orderByClause}

select

from eam_equipment t
LEFT JOIN eam_equipment_company e ON t.equipment_id = e.equipment_id
LEFT JOIN eam_equipment_model m ON t.equipment_model_id = m.equipment_model_id
WHERE t.delete_flag = 0 AND e.delete_flag = 0
AND m.protocol_id = 1

	AND e.company_id = #{companyId}


	AND t.equipment_id not in (
		select equipment_id from eam_dtu_equipment where dtu_id != #{dtuId} and delete_flag = 0
	)


	order by ${orderByClause}



		limit ${offset}, ${limit}


		limit ${limit}

select count(*) from eam_equipment t LEFT JOIN eam_equipment_company e ON t.equipment_id = e.equipment_id LEFT JOIN eam_equipment_model m ON t.equipment_model_id = m.equipment_model_id WHERE t.delete_flag = 0 AND e.delete_flag = 0 AND m.protocol_id = 1
AND e.company_id = #{companyId}
AND t.equipment_id not in ( select equipment_id from eam_dtu_equipment where dtu_id != #{dtuId} and delete_flag = 0 )
order by ${orderByClause}

select

from eam_equipment t
LEFT JOIN eam_equipment_company e ON t.equipment_id = e.equipment_id
LEFT JOIN eam_equipment_model m ON t.equipment_model_id = m.equipment_model_id
WHERE t.delete_flag = 0 AND e.delete_flag = 0
AND m.protocol_id = 4

	AND e.company_id = #{companyId}


	AND t.equipment_id not in (
	select equipment_id from eam_grm_equipment where grm_id != #{grmId} and delete_flag = 0
	)


	order by ${orderByClause}



		limit ${offset}, ${limit}


		limit ${limit}

select count(*) from eam_equipment t LEFT JOIN eam_equipment_company e ON t.equipment_id = e.equipment_id LEFT JOIN eam_equipment_model m ON t.equipment_model_id = m.equipment_model_id WHERE t.delete_flag = 0 AND e.delete_flag = 0 AND m.protocol_id = 4
AND e.company_id = #{companyId}
AND t.equipment_id not in ( select equipment_id from eam_grm_equipment where grm_id != #{grmId} and delete_flag = 0 )
order by ${orderByClause}

select DISTINCT
s.sensor_id, e.equipment_id, d.write_value from eam_equipment_model_properties p left join eam_sensor s on s.equipment_model_property_id = p.equipment_model_property_id left join eam_equipment e on e.equipment_model_id = p.equipment_model_id left join eam_sensor_write_data d on d.equipment_model_property_id = p.equipment_model_property_id AND d.equipment_id = e.equipment_id AND d.delete_flag = 0 WHERE s.delete_flag = 0 AND p.delete_flag = 0 AND e.delete_flag = 0 and e.equipment_id = #{equipmentId}
AND (p.name like #{pattern} or p.label like #{pattern} )
order by ${orderByClause}
limit ${offset}, ${limit}
limit ${limit}

select a.plan_id, work_content, next_maintain_date, maintain_frequency_unit, maintain_frequency_quantity, maintain_type, remind_time, a.create_time , ec.name as equipmentCategoryName, e.name as equipmentName, group_concat(uu.realname) AS maintainUsers from eam_maintain_plan a LEFT OUTER JOIN eam_equipment_category ec ON a.equipment_category_id = ec.equipment_category_id LEFT OUTER JOIN eam_equipment e ON a.equipment_id = e.equipment_id LEFT OUTER JOIN eam_maintain_user mu ON a.plan_id = mu.plan_id LEFT OUTER JOIN upms_user uu ON mu.user_id = uu.user_id WHERE a.delete_flag = 0 and mu.delete_flag = 0
AND a.company_id = #{companyId}
order by ${orderByClause}
limit ${offset}, ${limit}
limit ${limit}

select count(alarm_id) as alarmNum, IFNULL((select count(ticket_id) from eam_ticket where delete_flag=0 and company_id=#{ticketId, jdbcType=INTEGER}),0) as ticketNum,
IFNULL((select count(eq.equipment_id) from eam_equipment eq, eam_equipment_company c where eq.equipment_id = c.equipment_id and c.delete_flag=0 and c.company_id=#{ticketId, jdbcType=INTEGER}),0) as eqNum
from eam_alarm_record a where a.delete_flag=0 and a.company_id=#{ticketId, jdbcType=INTEGER}

select distinct e.name||','||a.alarm_type as message_title, r.alarm_value as content, r.alarm_id as id, 'E' as alarmType, r.alarm_status, r.update_time from eam_equipment e, eam_alarm a, eam_alarm_record r, eam_alarm_target_user u where e.equipment_id=r.equipment_id and r.alarm_id=a.alarm_id and r.alarm_id=u.alarm_id and u.user_id =#{userId, jdbcType=INTEGER}
UNION
SELECT message_title, content, id, 'M' as alarmType,'' as alarm_status,update_time FROM kuyun.eam_alert_message where user_id=#{userId, jdbcType=INTEGER} and read_flag is null and alert_start_date >= CURDATE() and alert_end_date >= CURDATE()
