select
e.lable_name as data_element_name,

from eam_grm_variable_data, eam_product_line p, eam_data_element e
WHERE eam_grm_variable_data.delete_flag = 0
AND p.delete_flag = 0 AND e.delete_flag = 0
AND eam_grm_variable_data.product_line_id = p.product_line_id
AND eam_grm_variable_data.data_element_id = e.id

	AND eam_grm_variable_data.product_line_id = #{productLineId}



	AND eam_grm_variable_data.equipment_id IS NULL



	AND eam_grm_variable_data.equipment_id = #{equipmentId}



	AND eam_grm_variable_data.data_group_id = #{dataGroupId}



	AND eam_grm_variable_data.equipmentDataGroupId = #{equipmentDataGroupId}



	AND eam_grm_variable_data.data_element_id = #{dataElementId}



	AND eam_grm_variable_data.update_time >= #{startDate}


	AND eam_grm_variable_data.update_time <=#{endDate}


	order by ${orderByClause}



		limit ${offset}, ${limit}


		limit ${limit}

select
e.lable_name as data_element_name,

from eam_grm_variable_data_history, eam_product_line p, eam_data_element e
WHERE eam_grm_variable_data_history.delete_flag = 0
AND p.delete_flag = 0 AND e.delete_flag = 0
AND eam_grm_variable_data_history.product_line_id = p.product_line_id
AND eam_grm_variable_data_history.data_element_id = e.id

	AND eam_grm_variable_data_history.product_line_id = #{productLineId}



	AND eam_grm_variable_data_history.equipment_id IS NULL



	AND eam_grm_variable_data_history.equipment_id = #{equipmentId}



	AND eam_grm_variable_data_history.data_group_id = #{dataGroupId}



	AND eam_grm_variable_data_history.equipmentDataGroupId = #{equipmentDataGroupId}



	AND eam_grm_variable_data_history.data_element_id = #{dataElementId}



	AND eam_grm_variable_data_history.update_time >= #{startDate}


	AND eam_grm_variable_data_history.update_time <=#{endDate}


	order by ${orderByClause}



		limit ${offset}, ${limit}


		limit ${limit}

