select plan_id, work_content, next_maintain_date, maintain_frequency_unit, maintain_frequency_quantity, maintain_type, remind_time, a.create_time , uo.name as orgName, ec.name as equipmentCategoryName, e.name as equipmentName from eam_maintain_plan a LEFT OUTER JOIN upms_organization uo ON a.org_id = uo.organization_id LEFT OUTER JOIN eam_equipment_category ec ON a.equipment_category_id = ec.equipment_category_id LEFT OUTER JOIN eam_equipment e ON a.equipment_id = e.equipment_id WHERE a.delete_flag = 0
AND a.company_id = #{companyId}
order by ${orderByClause}
limit ${offset}, ${limit}
limit ${limit}

SELECT t.description as ticketDescription, t.priority as ticketPriority, t.status as ticketStatus, uu.realname as serviceman, uu.phone as servicePhone, cu.realname as customerContacts, cu.phone as customerPhone, ass.assessment_level, ass.description as assessmentDescription, ass.id as assessmentId, mt.plan_id as planId, mt.id as planTicketId, eam_ticket_type.name as ticketTypeName from eam_maintain_ticket mt LEFT JOIN eam_ticket t on t.ticket_id = mt.ticket_id LEFT OUTER JOIN eam_ticket_type ON t.ticket_type_id = eam_ticket_type.id LEFT OUTER JOIN upms_user uu ON t.executor_id = uu.user_id LEFT OUTER JOIN upms_user cu ON t.create_user_id = cu.user_id LEFT OUTER JOIN eam_ticket_assessment ass ON t.ticket_id = ass.ticket_id where t.delete_flag = 0 and mt.delete_flag = 0
AND mt.plan_id = #{planId}
order by ${orderByClause}
limit ${offset}, ${limit}
limit ${limit}
