select
e.name as equipment_name, e.number as equipment_number, p.name as part_name, u.realname as maintain_user_name from eam_maintenance t LEFT JOIN eam_equipment e ON t.equipment_id = e.equipment_id LEFT JOIN eam_parts p on t.part_id = p.part_id LEFT JOIN upms_user u on t.maintain_user_id = u.user_id WHERE t.delete_flag = 0
AND t.company_id = #{companyId}
order by ${orderByClause}
limit ${offset}, ${limit}
limit ${limit}

select


e.name as warehouse_name
from eam_Location t
LEFT JOIN eam_warehouse e ON t.warehouse_id = e.warehouse_id
WHERE t.delete_flag = 0

	AND t.company_id = #{companyId}


	AND t.warehouse_id = #{warehouseId}


	order by ${orderByClause}



		limit ${offset}, ${limit}


		limit ${limit}

select

c.name as category_name
from eam_parts t
LEFT JOIN eam_parts_category c ON t.category_id = c.category_id
LEFT JOIN eam_equipment e ON t.equipment_id = e.equipment_id
WHERE t.delete_flag = 0 and e.delete_flag = 0

	AND e.equipment_id = #{equipmentId}


	AND e.product_line_id = #{productLineId}


	AND e.task_number = #{taskNumber}


	AND e.serial_number = #{serialNumber}


	AND t.symbol = #{symbol}


	AND t.category_id = #{categoryId}


	AND t.model = #{model}


	AND t.company_id = #{companyId}



	AND t.name like #{pattern}


	order by ${orderByClause}



		limit ${offset}, ${limit}


		limit ${limit}

select


w.name as warehouse_name,
l.number as location_number,
p.name as part_name
from eam_inventory t
LEFT JOIN eam_warehouse w ON t.warehouse_id = w.warehouse_id
LEFT JOIN eam_location l ON t.location_id = l.location_id
LEFT JOIN eam_parts p ON t.part_id = p.part_id
WHERE t.delete_flag = 0

	AND t.company_id = #{companyId}


	order by ${orderByClause}



		limit ${offset}, ${limit}


		limit ${limit}

select

e.name as equipment_name,
p.equipment_model_property_id,
p.name as equipment_model_property_name
from eam_sensor_data_history t
LEFT JOIN eam_sensor s ON t.sensor_id = s.sensor_id
LEFT JOIN eam_equipment_model_properties p ON p.equipment_model_property_id = s.equipment_model_property_id
LEFT JOIN eam_equipment e ON e.equipment_id = t.equipment_id
WHERE t.delete_flag = 0
AND s.delete_flag = 0
AND p.delete_flag = 0
AND e.delete_flag = 0

	AND t.equipment_id = #{equipmentId}


	AND t.sensor_id = #{sensorId}


	AND s.equipment_model_property_id = #{equipmentModelPropertyId}


	AND t.create_time >= #{startDate}


	AND t.create_time <=#{endDate}


	order by ${orderByClause}



		limit ${offset}, ${limit}


		limit ${limit}

SELECT DISTINCT
p.name, p.unit, p.data_type, p.display_type, case when e.is_online = 1 then d.string_value else null END string_value FROM eam_sensor s LEFT JOIN eam_equipment_model_properties p ON s.equipment_model_property_id = p.equipment_model_property_id LEFT JOIN eam_equipment_model m ON m.equipment_model_id = p.equipment_model_id LEFT JOIN eam_equipment e ON e.equipment_model_id = m.equipment_model_id LEFT JOIN eam_sensor_data d On s.sensor_id = d.sensor_id WHERE s.delete_flag = 0 AND p.delete_flag = 0 AND m.delete_flag = 0 AND e.delete_flag = 0 AND e.equipment_id = d.equipment_id AND e.equipment_id = #{equipmentId}
ORDER BY s.sensor_id ASC

select , uu.realname as serviceman, uu.phone as servicePhone, cu.realname as customerContacts, cu.phone as customerPhone, ass.assessment_level, ass.description as assessmentDescription, ass.id as assessmentId,
from eam_ticket LEFT OUTER JOIN eam_ticket_type ON eam_ticket.ticket_type_id = eam_ticket_type.id LEFT OUTER JOIN upms_user uu ON eam_ticket.executor_id = uu.user_id LEFT OUTER JOIN upms_user cu ON eam_ticket.create_user_id = cu.user_id LEFT OUTER JOIN eam_ticket_assessment ass ON eam_ticket.ticket_id = ass.ticket_id
order by ${orderByClause}
limit ${offset}, ${limit}
limit ${limit}

