select
e.unit,
e.lable_name as data_element_name,
egvd.value,
egvd.update_time,
e.data_type,
e.id as data_element_id
from eam_grm_variable_data egvd,
eam_data_element e
WHERE egvd.delete_flag = 0
AND e.delete_flag = 0
AND egvd.data_element_id = e.id

	AND egvd.product_line_id = #{productLineId}



	AND egvd.equipment_id is null
	AND egvd.data_element_id in (
		select egv.data_element_id from eam_grm_variable egv
		where egv.product_line_id = #{productLineId} and egv.equipment_id is null and egv.delete_flag = 0
	)



	AND egvd.equipment_id = #{equipmentId}
	AND egvd.data_element_id in (
		select egv.data_element_id from eam_grm_variable egv
		where egvd.product_line_id = egv.product_line_id
		and egvd.equipment_id = egv.equipment_id
		and egvd.data_group_id = egv.data_group_id
		and egvd.equipment_data_group_id = egv.equipment_data_group_id
		and egv.delete_flag = 0
	)



	AND egvd.data_group_id = #{dataGroupId}



	AND egvd.equipmentDataGroupId = #{equipmentDataGroupId}



	AND egvd.data_element_id = #{dataElementId}



	AND egvd.update_time >= #{startDate}


	AND egvd.update_time <=#{endDate}


	order by ${orderByClause}



		limit ${offset}, ${limit}


		limit ${limit}

select
DISTINCT
e.unit,
e.lable_name,
egvdh.data_element_id,
egvdh.value,
egvdh.update_time
from eam_grm_variable_data_history egvdh,
eam_data_element e
WHERE egvdh.delete_flag = 0
AND e.delete_flag = 0
AND egvdh.data_element_id = e.id


	AND egvdh.product_line_id = #{productLineId}



	AND egvdh.equipment_id IS NULL
	AND egvdh.data_element_id in (
	select egv.data_element_id from eam_grm_variable egv
	where egv.product_line_id = #{productLineId} and egv.equipment_id is null and egv.delete_flag = 0
	)



	AND egvdh.equipment_id = #{equipmentId}
	AND egvdh.data_element_id in (
	select egv.data_element_id from eam_grm_variable egv
	where egvdh.product_line_id = egv.product_line_id
	and egvdh.equipment_id = egv.equipment_id
	and egvdh.data_group_id = egv.data_group_id
	and egvdh.equipment_data_group_id = egv.equipment_data_group_id
	and egv.delete_flag = 0
	)



	AND egvdh.data_group_id = #{dataGroupId}



	AND egvdh.equipmentDataGroupId = #{equipmentDataGroupId}



	AND egvdh.data_element_id = #{dataElementId}



	AND egvdh.data_element_id IN

		#{item}






			AND egvdh.update_time >= #{startDate}

			AND (time_to_sec(timediff(egvdh.update_time, #{startDate})) mod #{interval} < #{grmPeriod})


			AND egvdh.update_time >= #{startDate}





	AND egvdh.update_time <=#{endDate}


	order by ${orderByClause}



		limit ${offset}, ${limit}


		limit ${limit}

