select plan_id, work_content, next_maintain_date, maintain_frequency_unit, maintain_frequency_quantity, maintain_type, remind_time, a.create_time , uo.name as orgName, ec.name as equipmentCategoryName, e.name as equipmentName from eam_maintain_plan a LEFT OUTER JOIN upms_organization uo ON a.org_id = uo.organization_id LEFT OUTER JOIN eam_equipment_category ec ON a.equipment_category_id = ec.equipment_category_id LEFT OUTER JOIN eam_equipment e ON a.equipment_id = e.equipment_id WHERE a.delete_flag = 0
AND a.company_id = #{companyId}
order by ${orderByClause}
limit ${offset}, ${limit}
limit ${limit}

SELECT t.description as ticketDescription, t.priority as ticketPriority, t.status as ticketStatus, uu.realname as serviceman, uu.phone as servicePhone, cu.realname as customerContacts, cu.phone as customerPhone, ass.assessment_level, ass.description as assessmentDescription, ass.id as assessmentId, mt.plan_id as planId, mt.id as planTicketId, eam_ticket_type.name as ticketTypeName from eam_maintain_ticket mt LEFT JOIN eam_ticket t on t.ticket_id = mt.ticket_id LEFT OUTER JOIN eam_ticket_type ON t.ticket_type_id = eam_ticket_type.id LEFT OUTER JOIN upms_user uu ON t.executor_id = uu.user_id LEFT OUTER JOIN upms_user cu ON t.create_user_id = cu.user_id LEFT OUTER JOIN eam_ticket_assessment ass ON t.ticket_id = ass.ticket_id where t.delete_flag = 0 and mt.delete_flag = 0
AND mt.plan_id = #{planId}
order by ${orderByClause}
limit ${offset}, ${limit}
limit ${limit}

select
 ,
(select code_name from eam_code_value where state = code_value and category = 'STATE') as stateName,
(select code_name from eam_code_value where industry = code_value and category = 'INDUSTRY') as industryName,
(select code_name from eam_code_value where product_line_type = code_value and category = 'PRODUCT_LINE_TYPE') as productLineTypeName,
(select code_name from eam_code_value where product_line_capacity = code_value and category = 'PRODUCT_LINE_CAPACITY') as productLineCapacityName,
(select code_name from eam_code_value where packaging_material = code_value and category = 'PACKAGING_MATERIAL') as packagingMaterialName,
(select code_name from eam_code_value where product_spec = code_value and category = 'PRODUCT_SPEC') as productSpecName
from eam_order
WHERE 1 = 1


	AND company_name like #{pattern}



	AND industry = #{industry}


	AND product_line_type = #{productLineType}


	AND year = #{year}


	AND country = #{country}


	AND province = #{province}


	AND city = #{city}


	order by ${orderByClause}



		limit ${offset}, ${limit}


		limit ${limit}

select count(id)
from eam_order
WHERE 1 = 1


	AND company_name like #{pattern}



	AND industry = #{industry}


	AND product_line_type = #{productLineType}


	AND year = #{year}


	AND country = #{country}


	AND province = #{province}


	AND city = #{city}

select
p.name as product_line_name,
(select name from eam_equipment e  where e.equipment_id = eam_grm_variable.equipment_id) as equipment_name,

from eam_grm_variable
LEFT JOIN eam_product_line p ON eam_grm_variable.product_line_id = p.product_line_id AND p.delete_flag = 0
WHERE eam_grm_variable.delete_flag = 0

	AND eam_grm_variable.product_line_id = #{productLineId}


	order by ${orderByClause}



		limit ${offset}, ${limit}


		limit ${limit}

select count(eam_grm_variable.id) from eam_grm_variable LEFT JOIN eam_product_line p ON eam_grm_variable.product_line_id = p.product_line_id AND p.delete_flag = 0 WHERE eam_grm_variable.delete_flag = 0
AND eam_grm_variable.product_line_id = #{productLineId}
order by ${orderByClause}
