mapper.select:
select

from upms_permission up, (
select permission_id permissionId from upms_role_permission urp, upms_user_role uur where urp.role_id =uur.role_id AND uur.user_id= #{upmsUserId,jdbcType=INTEGER}
union
select permission_id permissionId from upms_user_permission uup1 where uup1.user_id= #{upmsUserId,jdbcType=INTEGER} and uup1.type=1
) p where up.`status`=1 and up.permission_id = p.permissionId
and up.permission_id not in (select permission_id from upms_user_permission uup2 where uup2.permission_id and uup2.user_id= #{upmsUserId,jdbcType=INTEGER} and uup2.type=-1)
order by up.orders asc

select

from upms_role ur where ur.role_id in (
select uur.role_id from upms_user_role uur where uur.user_id=#{upmsUserId,jdbcType=INTEGER}
)

select

from upms_user ur where ur.user_id in (
select uur.user_id from upms_user_role uur where uur.role_id=#{upmsRoleId,jdbcType=INTEGER}
)

select uu.user_id, uu.realname, uu.phone, uu.email, uc.name as company_name, max(ul.start_time) as login_time, count(ul.log_id) as login_number from upms_user uu left join upms_user_company uuc on uu.user_id = uuc.user_id left join upms_company uc on uc.company_id = uuc.company_id left join upms_log ul on uu.username = ul.username and method = 'POST' AND description = '登录' GROUP BY uu.user_id, uu.realname, uu.phone, uu.email, uc.name
order by ${orderByClause}
limit ${offset}, ${limit}
limit ${limit}

select uc.name, count(ee.equipment_id) as equipment_number, sum(case is_online when 1 then 1 else 0 end) as online_number, count(ee.equipment_id) - sum(case is_online when 1 then 1 else 0 end) as offline_number from upms_company uc left join eam_equipment_company eec on uc.company_id = eec.company_id and eec.delete_flag = 0 left join eam_equipment ee on eec.equipment_id = ee.equipment_id and ee.delete_flag = 0 where uc.delete_flag = 0 group by uc.name
order by ${orderByClause}
limit ${offset}, ${limit}
limit ${limit}

select DISTINCT uu.*
from upms_user uu
left join upms_user_company uuc on uu.user_id = uuc.user_id
left join upms_user_organization uuo on uu.user_id = uuo.user_id
left join upms_organization uo on uo.organization_id = uuo.organization_id
WHERE 1 = 1

	AND uuc.company_id = #{companyId}


	AND uo.name = #{orgName}


	order by ${orderByClause}

select distinct uu.user_id, uu.realname, uu.phone, uu.email, uu.avatar from upms_user uu, upms_user_company uuc, upms_organization uo, upms_user_organization uuo where uu.user_id = uuc.user_id and uuc.company_id=uo.company_id and uu.user_id = uuo.user_id and uo.organization_id = uuo.organization_id
and uo.company_id = ${companyId}
and uo.name = '${orgName}'
order by ${orderByClause}

select	uu.user_id, uu.username, uu.password, uu.salt, uu.realname, uu.avatar, uu.phone, uu.email, uu.sex, uu.locked, uu.ctime
from upms_user uu
left join upms_user_company uuc on uu.user_id = uuc.user_id
WHERE 1 = 1

	AND uuc.company_id = #{companyId}



	AND (uu.username like #{pattern} OR uu.realname like #{pattern})


	order by ${orderByClause}



		limit ${offset}, ${limit}


		limit ${limit}

select count(uu.user_id) from upms_user uu left join upms_user_company uuc on uu.user_id = uuc.user_id WHERE 1 = 1
AND uuc.company_id = #{companyId}
AND (uu.username like #{pattern} OR uu.realname like #{pattern})
order by ${orderByClause}

select
uuc.company_id, uuc.seq_id, uuc.parent_id, uuc.name, uuc.phone, uuc.fax, uuc.zip, uuc.www, uuc.address, uuc.admin_name, uuc.admin_password,
uuc.create_user_id, uuc.create_time, uuc.update_user_id, uuc.update_time, uuc.delete_flag
from upms_company uuc
WHERE uuc.delete_flag = 0


	AND uuc.name like #{pattern}


	AND uuc.parent_id = #{parentId}


	order by ${orderByClause}



		limit ${offset}, ${limit}


		limit ${limit}

select count(uuc.company_id)
from upms_company uuc
WHERE uuc.delete_flag = 0


	AND uuc.name like #{pattern}


	AND uuc.parent_id = #{parentId}


	order by ${orderByClause}
