mapper.select:
		select

		from upms_permission up where up.`status`=1 and up.permission_id in (
			select permission_id from upms_role_permission urp where urp.role_id in (
				select uur.role_id role_id from upms_user_role uur where uur.user_id=#{upmsUserId,jdbcType=INTEGER}
                union
                select uor.role_id from upms_organization_role uor, upms_user_organization uuo
                where uor.organization_id= uuo.organization_id and uuo.user_id = #{upmsUserId,jdbcType=INTEGER}
			)
			union
			select permission_id from upms_user_permission uup1 where uup1.user_id=#{upmsUserId,jdbcType=INTEGER} and uup1.type=1
		)
		and up.permission_id not in (
			select permission_id from upms_user_permission uup2 where uup2.user_id=#{upmsUserId,jdbcType=INTEGER} and uup2.type=-1
		) order by up.orders asc

select

from upms_role ur where ur.role_id in (
select uur.role_id from upms_user_role uur where uur.user_id=#{upmsUserId,jdbcType=INTEGER}
)

select

from upms_user ur where ur.user_id in (
select uur.user_id from upms_user_role uur where uur.role_id=#{upmsRoleId,jdbcType=INTEGER}
)

select uu.user_id, uu.realname, uu.phone, uu.email, uc.name as company_name, max(ul.start_time) as login_time, count(ul.log_id) as login_number from upms_user uu left join upms_user_company uuc on uu.user_id = uuc.user_id left join upms_company uc on uc.company_id = uuc.company_id left join upms_log ul on uu.username = ul.username and method = 'POST' AND description = '登录' GROUP BY uu.user_id, uu.realname, uu.phone, uu.email, uc.name
order by ${orderByClause}
limit ${offset}, ${limit}
limit ${limit}

select uc.name, count(ee.equipment_id) as equipment_number, sum(case is_online when 1 then 1 else 0 end) as online_number, count(ee.equipment_id) - sum(case is_online when 1 then 1 else 0 end) as offline_number from upms_company uc left join eam_equipment_company eec on uc.company_id = eec.company_id and eec.delete_flag = 0 left join eam_equipment ee on eec.equipment_id = ee.equipment_id and ee.delete_flag = 0 where uc.delete_flag = 0 group by uc.name
order by ${orderByClause}
limit ${offset}, ${limit}
limit ${limit}

select distinct uu.user_id, uu.realname, uu.phone, uu.email
from upms_user uu, upms_user_company uuc, upms_organization uo where uu.user_id = uuc.user_id and uuc.company_id=uo.company_id

    and  uo.organization_id = ${orgId}


    order by ${orderByClause}



        limit ${offset}, ${limit}


        limit ${limit}

select distinct uu.user_id, uu.realname, uu.phone, uu.email
from upms_user uu, upms_user_company uuc, upms_organization uo where uu.user_id = uuc.user_id and uuc.company_id=uo.company_id

    and  uo.company_id = ${companyId}


    and  uo.name = '${orgName}'


    order by ${orderByClause}

select count(distinct uu.user_id)
from upms_user uu, upms_user_company uuc, upms_organization uo where uu.user_id = uuc.user_id and uuc.company_id=uo.company_id
and  uo.organization_id = #{orgId,jdbcType=INTEGER}

select distinct r.role_id, r.name, r.title, r.description, IFNULL(uor.role_id, FALSE) as checked
from upms_role r
left  join upms_organization_role uor on r.role_id = uor.role_id
left  join upms_organization o on uor.organization_id=o.organization_id

    where  o.company_id = ${companyId}


    and  o.organization_id = ${orgId}


    order by ${orderByClause}



        limit ${offset}, ${limit}


        limit ${limit}
